<?$userID = $u[id];$selS = 'select *, date_format(purchased, "%m/%d/%Y") as purchased from sales where affiliate="' . $userID . '" order by purchased';$resS = mysql_query($selS, $conn) or die(mysql_error());if (mysql_num_rows($resS) == 0) {    $salesContent .= '<tr>    <td>No sales yet</td>    </tr>';} else {    while ($s = mysql_fetch_assoc($resS)) {        $salesContent .= '<tr>         <td>' . $s[purchased] . '</td>        <td title="' . $s[itemName] . '">' . $s[itemNumber] . '</td>        <td title="' . $s[firstName] . ' ' . $s[lastName] . '">' . $s[payerEmail] . '</td>        <td>' . $s[paidTo] . '</td>        </tr>';    }}?><p>&nbsp;</p><center>    <table class="moduleBlue">        <tr>            <th colspan=5>Sales Detail</th>        </tr>        <tr>            <td>Purchase Date</td>            <td>Product Name</td>            <td>Customer Email </td>            <td>Paid To?</td>        </tr><?= $salesContent ?>     </table></center></p>&nbsp;</p>